            );
            me.log('\n');
            p.exit(0);
        });
    }
    static setDebugLevel(b) {
        ENABLE_DEBUG = b;
    }
}

function __carriage__() {
    let args = Array.prototype.slice.call(arguments);
    return args.map((v) => v.replace ? v.replace('\r\n', ' ') : v);
}

