/**
 * @module Angie.js
 * @author Joe Groseclose <@benderTheCrime>
 * @date 8/16/2015
 */

// System Modules
import fs from                                          'fs';
import {magenta, blue} from                             'chalk';
import $LogProvider from                                'angie-log';
import {$injectionBinder} from                          'angie-injector';

// Angie Modules
import {config} from                                    './Config';
import {$scope} from                                    './controllers/$ScopeProvider';
import $RouteProvider from                              './factories/$RouteProvider';
import $CacheFactory from                               './factories/$CacheFactory';
import $compile from                                    './factories/$Compile';
import {$templateCache, $resourceLoader} from           './factories/$TemplateCache';
import {$StringUtil} from                               './util/Util';
import * as $ExceptionsProvider from                    './util/$ExceptionsProvider';

/**
 * @desc This is the default Angie class. It is instantiated and given
 * the namespace `global.app`. Static methods are available via this
 * instance.
 *
 * It is ill advised to tray and redefine the native app as it is tightly coupled
 * with the resource pipeline & webserver, instead, use the Angie class to
 * access commonly used static methods.
 *
 * @todo rename this class
 * @since 0.0.1
 * @access public
 * @extends $Util
 * @example Angie.noop() // = undefined
 */
class Angie {
    constructor() {
        this.constants = {};
        this.configs = [];
        this.services = {};
        this.factories = {};
        this.Controllers = {};
        this.directives = {};
        this.$dependencies = [];
        this.$$registry = {};
        this.$$loaded = false;
    }

    /**
     * @desc Creates an Angie constant provider
     *
     * @since 0.0.1
     * @access public
     *
     * @param {string} name The name of the constant being created
     * @param {object|string|number|Array<>|boolean} obj The object value
     * @returns {object} this instanceof Angie
     *
     * @example Angie.constant('foo', 'bar');
     */
    constant(name, obj) {
        return this.$$register('constants', name, obj);
    }

    /**
     * @desc Creates an Angie service provider. This must be an object.
     *
     * @since 0.0.1
     * @access public
     *
     * @param {string} name The name of the service being created
     * @param {object} obj The object value
     * @returns {object} this instanceof Angie
     *
     * @example Angie.service('foo', {});
     */
    service(name, obj) {

        // Verify that the service is an object
        if (typeof obj !== 'object') {
            throw new $ExceptionsProvider.$$InvalidServiceConfigError(name);
        }
        return this.$$register('services', name, obj);
    }

    /**
     * @desc Creates an Angie service provider. This must be a function.
     *
     * @since 0.3.1
     * @access public
     *
     * @param {string} name The name of the factory being created
     * @param {function} fn The function value
     * @returns {object} this instanceof Angie
     *
     * @example Angie.factory('foo', () => undefined);
     */
    factory(name, fn) {

        // Verify that the factory is a function
        if (typeof fn !== 'function' || !fn.prototype.constructor) {
            throw new $ExceptionsProvider.$$InvalidFactoryConfigError(name);
        }
        return this.$$register('factories', name, fn);
    }

    /**
     * @desc Creates an Angie Controller provider. This must be a function that
     * returns an object or an object.
     *
     * @since 0.0.1
     * @access public
     *
     * @param {string} name The name of the factory being created
     * @param {function|object} obj The Controller value
     * @param {string} obj.template An actual HTML template to be added as a
     * byproduct of the Controller
     * @param {string} obj.templatePath The path to an HTML template to be
     * added as a byproduct of the Controller
     * @returns {object} this instanceof Angie
     *
     * @example Angie.Controller('foo', () => {});
     */
    Controller(name, obj) {
        return this.$$register('Controllers', name, obj);
    }

    /**
     * @desc Creates an Angie directive provider. The second parameter
     * of the directive function must be an object, with properties defining the
     * directive itself.
     *
     * @since 0.2.3
     * @access public
     *
     * @param {string} name The name of the constant being created
     * @param {function|object} obj The directive value, returns directive params
     * @param {string} obj().Controller The associated directive controller
     * @param {number} obj().priority A number representing the directive's
     * priority, relative to the other declared directives
     * @param {boolean} obj().replace Does the directive root get replaced by
     * its inner HTML?
     * @param {string} obj().restrict What HTML components can parse this directive:
     *    'A': attribute
     *    'E': element
     *    'C': class
     * @param {function} obj().link A function to fire after the directive is
     * parsed
     * @param {string} obj().template An actual HTML template to be added as a
     * byproduct of the directive
     * @param {string} obj().templatePath The path to an HTML template to be
     * added as a byproduct of the directive
     * @returns {object} this instanceof Angie
     *
     * @example Angie.directive('foo', {
     *     return {
     *         Controller: 'test',
     *         link: function() {}
     *     };
     * });
     */
    directive(name, obj) {
        const dir = typeof obj !== 'function' ?
            obj : new $injectionBinder(obj, 'directive')();

        if (dir.hasOwnProperty('Controller')) {
            if (typeof dir.Controller !== 'string') {
                delete dir.Controller;
            }
        } else if (/api.?view/i.test(dir.type)) {
            throw new $ExceptionsProvider.$$InvalidDirectiveConfigError(name);
        }
        return this.$$register('directives', name, dir);
    }
    config(fn) {
        if (typeof fn === 'function') {
            this.configs.push({
                fn: fn,
                fired: false
            });
        } else {
            $LogProvider.warn('Invalid config type specified');
        }
        return this;
    }
    $$register(component, name, obj) {

        // `component` and `app.component` should always be defined
        if (name && obj) {
            this.$$registry[ name ] = component;
            this[ component ][ name ] = obj;
        } else {
            $LogProvider.warn(
                `Invalid name or object ${name} called on app.${component}`
            );
        }
        return this;
    }

    // Tear down a registered component
    $$tearDown(name) {
        if (name && this.$$registry[ name ]) {
            const type = this.$$registry[ name ];
            delete this.$$registry[ name ];
            delete this[ type ][ name ];
        }
        return this;
    }

    /**
     * @desc Load all project dependencies from an Array of dependencies
     * specified in the AngieFile.json. This will load packages in the order
     * they are specified, exposing any application Modules to the application
     * and prepping any application configuration in the nested modules. It will
     * not load duplicate modules. Dependencies are typically declared as a
     * node_module path, but can also be declared as a singular (main) file.
     * @since 0.1.0
     * @access private
     * @param {object}  [param=[]] dependencies The Array of dependencies
     * specified in the parent or localized AngieFile.json
     */
    $$loadDependencies(dependencies = []) {
        let me = this,
            proms = [];

        // Make sure we do not load duplicate dependencies
        dependencies = dependencies.filter(
            (v) => me.$dependencies.indexOf(v) === -1
        );

        // Add dependencies
        this.$dependencies = this.$dependencies.concat(dependencies);
        dependencies.forEach(function(v) {
            let dependency = $StringUtil.removeTrailingLeadingSlashes(v),

                // This will load all of the modules, overwriting a module name
                // will replace it
                prom = new Promise(function(resolve) {
                    let $config,
                        name,
                        subDependencies;

                    try {
                        $config = JSON.parse(
                            fs.readFileSync(
                                `./node_modules/${dependency}/AngieFile.json`,
                                'utf8'
                            )
                        );
                    } catch(e) {
                        $LogProvider.error(e);
                    }

                    if (typeof $config === 'object') {

                        // Grab the dependency name fo' reals
                        name = $config.projectName;

                        // Find any sub dependencies for recursive module
                        // loading
                        subDependencies = config.dependencies;

                        // Set the config in dependency configs (just in case)
                        if (!app.$dependencyConfig) {
                            app.$dependencyConfig = {};
                        }
                        app.$dependencyConfig[ dependency ] = $config;
                    }

                    try {

                        let service = require(dependency);

                        // TODO make this try to load not an npm project config
                        if (service) {
                            me[
                                typeof service === 'function' ? 'factory' :
                                    typeof service === 'object' ? 'service' :
                                        'constant'
                            ](
                                name || $StringUtil.toCamel(dependency),
                                require(dependency)
                            );
                            $LogProvider.info(
                                `Successfully loaded dependency ${magenta(v)}`
                            );
                        }
                    } catch(e) {
                        $LogProvider.error(e);
                    }

                    return app.$$loadDependencies(
                        subDependencies || []
                    ).then(resolve);
                });
            proms.push(prom);
        });
        return Promise.all(proms);
    }

    /**
     * @desc Load all of the files associated with the parent and child Angie
     * applications. This will transpile and deliver all modules associated with
     * both the parent and child applications.
     * @since 0.1.0
     * @access private
     * @param {string}  [param=process.cwd()] dir The dir to scan for modules
     */
    $$bootstrap(dir = process.cwd()) {
        let me = this,
            src = typeof config.projectRoot === 'string' ?
                $StringUtil.removeTrailingLeadingSlashes(config.projectRoot) :
                'src';

        return new Promise(function(resolve) {
            resolve(
                fs.readdirSync(`${dir}/${src}`).map((v) => `${dir}/src/${v}`)
            );
        }).then(function(files) {
            let proms = [],
                fn = function loadFiles(files) {

                    // We don't want to load any of these files
                    files.forEach(function(v) {
                        if (
                            /node_modules|bower_components|templates|static/i
                            .test(v)
                        ) {

                            // If any part of our url contains these return
                            return;
                        } else if (
                            [ 'js', 'es6' ].indexOf(v.split('.').pop() || '') > -1
                        ) {
                            try {
                                require(v);
                                $LogProvider.info(
                                    `Successfully loaded file ${blue(v)}`
                                );
                            } catch(e) {
                                $LogProvider.error(e);
                            }
                        } else {
                            try {
                                fn(fs.readdirSync(v).map(($v) => `${v}/${$v}`));
                            } catch(e) {
                                $LogProvider.warn(
                                    `Treating ${blue(v)} as a directory, but it is a file`
                                );
                            }
                        }
                    });
                };
            fn(files);
            return Promise.all(proms);
        }).then(function() {

            // Once all of the modules are loaded, run the configs
            me.configs.forEach(function(v) {

                // Check to see if the config has already fired, if it has, we
                // do not want to fire it again
                if (!v.fired) {
                    new $injectionBinder(v.fn, 'config')();

                    // Mark as fired
                    v.fired = true;
                }
            });
        });
    }
    $$load() {
        let me = this;

        // Do not call load twice
        if (this.$$loaded === true) {
            return new Promise((r) => { r(); });
        }

        // Load any app dependencies
        return this.$$loadDependencies(config.dependencies).then(function() {

            // Set the app in a loaded state
            me.$$loaded = true;

            // Bootstrap the application
            me.$$bootstrap();
        });
    }
}

let app = global.app = new Angie();
app.config(function() {
    $templateCache.put(
        'index.html',
        fs.readFileSync(`${__dirname}/templates/html/index.html`, 'utf8')
    );
    $templateCache.put(
        '404.html',
        fs.readFileSync(`${__dirname}/templates/html/404.html`, 'utf8')
    );
})
.factory('$Routes', $RouteProvider)
.factory('$Cache', $CacheFactory)
.factory('$compile', $compile)
.factory('$resourceLoader', $resourceLoader)

// Error utilities
.service('$Exceptions', $ExceptionsProvider)

// TODO we shouldn't have to expose this?
.service('$scope', $scope)
.service('$window', {})
.service('$document', {})
.service('$templateCache', $templateCache);

export default app;
export {Angie};

/**
 * @module Config.js
 * @author Joe Groseclose <@benderTheCrime>
 * @date 8/16/2015
 */

// System Modules
import fs from                          'fs';

// Angie Modules
import {$FileUtil} from                 './util/Util';
import {$$InvalidConfigError} from      './util/$ExceptionsProvider';

let config = {};

class Config {
    constructor() {
        if (Object.keys(config).length === 0) {
            return new Promise(function(resolve, reject) {

                // Use the file finder to check against filetype
                let fileNames = [
                        'angiefile',
                        'angieFile',
                        'Angiefile',
                        'AngieFile'
                    ],
                    acceptedFileNames = [],
                    file;

                fileNames.forEach(function(name) {
                    acceptedFileNames.push(`${name}.json`);
                    acceptedFileNames.push(`${name}.js`);
                    acceptedFileNames.push(`${name}.es6`);
                });

                acceptedFileNames.forEach(function(name) {
                    let tmpFile = $FileUtil.find(process.cwd(), name);
                    if (tmpFile) {
                        file = tmpFile;
                    }
                });

                try {
                    resolve(fs.readFileSync(file, 'utf8'));
                } catch(e) {
                    reject(e);
                }
            }).then(function(stdout) {
                config = JSON.parse(stdout);
                if (global.app) {
                    global.app.$$config = Object.freeze(config);
                }
            }).catch(() => { throw new $$InvalidConfigError(); });
        } else {
            return new Promise(() => arguments[0]());
        }
    }
}

export default Config;
export {config};

/**
 * @module server.js
 * @author Joe Groseclose <@benderTheCrime>
 * @date 8/16/2015
 */

// System Modules
import http from                    'http';
import https from                   'https';
import url from                     'url';
import util from                    'util';
import watch from                   'node-watch';
import $LogProvider from            'angie-log';

// Angie Modules
import {config} from                './Config';
import app from                     './Angie';
import $CacheFactory from           './factories/$CacheFactory';
import {$$templateLoader} from      './factories/$TemplateCache';
import {BaseRequest} from           './services/BaseRequest';
import {default as $MimeType} from  './util/$MimeTypeProvider';

const p = process;
let firstrun = true;

function server(args) {
    const useSSL = /\--?usessl/i.test(args),
          port = useSSL ? 443 : !isNaN(args[1]) ? +args[1] : 3000;

    app.$$load().then(function() {

        // Start a webserver
        // TODO run the webserver with Gulp and gulp watch project files and angie files to reload
        (useSSL ? https : http).createServer(function(request, response) {
            const path = url.parse(request.url).pathname;
            let angieResponse = new BaseRequest(path, request, response),
                asset;

            // A file cannot be in the static path if it doesn't have an extension, shortcut
            // TODO you may want to move the asset loading block out of here
            if (path.indexOf('.') > -1) {
                let assetCache = new $CacheFactory('staticAssets');

                if (assetCache.get(path)) {
                    asset = assetCache.get(path);
                } else {
                    asset = $$templateLoader(path, 'static');
                }

                // We have an asset and must render a response
                if (asset) {

                    // Set the content type
                    angieResponse.responseHeaders[ 'Content-Type' ] =
                        $MimeType.fromPath(path);

                    // We do not want to cache responses
                    if (
                        config.hasOwnProperty('cacheStaticAssets') &&
                        !config.cacheStaticAssets
                    ) {
                        angieResponse.responseHeaders = util._extend(
                            angieResponse.responseHeaders,
                            {
                                Expires: -1,
                                Pragma: app.constants.PRAGMA_HEADER,
                                'Cache-Control': app.constants.NO_CACHE_HEADER
                            }
                        );
                    }

                    response.writeHead(
                        200,
                        app.constants.RESPONSE_HEADER_MESSAGES[ '200' ],
                        angieResponse.responseHeaders
                    );

                    // Check if you have an image type asset
                    $LogProvider.info(path, response._header);
                    response.write(asset);
                }

                // End the response
                response.end();
                return;
            }

            // else {

            angieResponse.$$route().then(function() {
                let code = response.statusCode;
                if (!code) {
                    const error = $$templateLoader('500.html');

                    // TODO extrapolate this to responses
                    response.writeHead(
                        500,
                        app.constants.RESPONSE_HEADER_MESSAGES[ '500' ],
                        angieResponse.responseHeaders
                    );
                    response.write(error);
                    $LogProvider.error(path, response._header);
                } else if (code < 400) {
                    $LogProvider.info(path, response._header);
                } else if (code < 500) {
                    $LogProvider.warn(path, response._header);
                } else {
                    $LogProvider.error(path, response._header);
                }
                return true;
            }).then(function() {

                // End the response
                response.end();

                // TODO this seems to cause ERR_INCOMPLETE_CHUNKED_ENCODING
                // request.connection.end();
                // request.connection.destroy();
            });

            // }

        }).listen(port);

        // Attempt to restart the webserver on change
        if (firstrun) {
            let watchDirs = [ p.cwd(), __dirname ].concat(app._$dependencies__);

            try {
                let restartObj = {
                        persistent: true,
                        recursive: true
                    };
                watch(watchDirs, (() => restart(port)), restartObj);
            } catch(e) {
                $LogProvider.error(e);
            }
        }

        // Info
        $LogProvider.info(`Serving on port ${port}`);

        // Set firstrun to false
        firstrun = false;
    });
}

function restart(port) {

    // TODO this doesn't reload like you think it does
    app.$$load().then(function() {
        $LogProvider.info(
            `Application files reloaded; Still serving on port ${port}`
        );
    });
}

export default server;

/**
 * @module $ScopeProvider.js
 * @author Joe Groseclose <@benderTheCrime>
 * @date 8/16/2015
 */

let handlers = [];

/**
 * @desc $ScopeProvider is the parent class on which all of the content shared
 * across the request can be shared. It also provides methods for messaging
 * against and across the scope.
 * @since 0.3.1
 * @access private
 */
class $$ScopeProvider {
    constructor() {
        this.$$id = 1;
    }

    /**
     * @desc Sets up an event listener on the scope
     * @since 0.3.1
     * @param {string} name The name of the message to call the event listener
     * @param {function} fn The handler to call on message
     * @returns {boolean} A representation of whether the listener was attached
     * @access public
     * @example $scope.$on('test', () => undefined);
     */
    $on(name, fn) {
        if (typeof fn === 'function') {
            handlers.push({ [ name ]: fn });
            return true;
        }
        return false;
    }

    /**
     * @desc Calls an event listener on the scope
     * @since 0.3.1
     * @param {string} name The name of the message to call the event listener
     * @param {boolean} async Should the handlers be called async?
     * @returns {boolean} A representation of whether the listeners were called
     * @access public
     * @example $scope.$broadcast('test', true);
     */
    $broadcast(name, async) {
        let args = Array.from(arguments).splice(0, 1);
        for (let handler of handlers) {
            if (handler.hasOwnProperty(name)) {
                if (async) {
                    setImmediate(handler[ name ].bind(null, args));
                } else {
                    handler[ name ](args);
                }
            }
        }
        return true;
    }

    /**
     * @desc Sets up an observer on an object (shallow). This function does not
     * support dots.
     * @since 0.3.1
     * @todo deep watch
     * @param {object} obj The object upon which to set up the handler
     * @param {function} fn The handler to call on message
     * @returns {object} The passed object
     * @access public
     * @example $scope.$watch({}, () => undefined);
     */
    $watch(key, fn) {
        return Object.observe(this[ key ], fn);
    }

    /**
     * @desc Sets up an observer on an object (deep). This function does not
     * support dots.
     * @since 0.3.1
     * @param {object} obj The object upon which to set up the handler
     * @param {function} fn The handler to call on message
     * @access public
     * @example $scope.$watchCollection({ test: {} }, () => undefined);
     */
    // $watchCollection(key, fn) {
    //     let me = this;
    //     Object.observe(this[ key ], function(changes) {
    //         changes.forEach(function(change) {
    //             if (typeof me[ key ][ change.name ] === 'object') {
    //                 me.$watch(me[ key ][ change.name ], fn);
    //             }
    //         });
    //         fn.apply(me, arguments);
    //     });
    // }

    /**
     * @desc Removes all of the handlers from a listener
     * @since 0.3.1
     * @param {string} name The name of the message to call the event listener
     * @returns {boolean} A representation of whether the listeners were attached
     * @access public
     * @example $scope.$off('test');
     */
    $$off(name) {
        for (let handler of handlers) {
            if (handler.hasOwnProperty(name)) {
                handlers.splice(handlers.indexOf(handler), 1);
            }
        }
        return true;
    }

    /**
     * @desc Removes all of the watchers from an object
     * @since 0.3.1
     * @todo deep unwatch
     * @param {object} obj The object from which to remove the handler
     * @returns {boolean} A representation of whether the listeners were unattached
     * @access public
     * @example $scope.$$removeWatch({});
     */
    $$removeWatch(obj) {
        Object.unwatch(obj);

        return true;
    }

    /**
     * @desc Sets up or removes all handlers
     * @since 0.3.1
     * @param {Array} $handlers An optional list of handlers
     * @returns {Array} handlers
     * @access private
     */
    $$handlers($handlers) {
        return (handlers = $handlers || handlers);
    }
}

/**
 * @desc $scope is the instance of $$ScopeProvider allocated to resources
 * @since 0.3.1
 * @access private
 */
const $scope = new $$ScopeProvider();
export default $$ScopeProvider;
export {$scope};

/**
 * @module $CacheFactory.js
 * @author Joe Groseclose <@benderTheCrime>
 * @date 8/16/2015
 */

let cache = {};

class $CacheFactory {
    constructor(key) {
        this.key = key;
        if (!cache[ key ]) {
            this.cache = cache[ key ] = {};
        } else {
            this.cache = cache[ key ];
        }
    }
    put(id, obj, replace) {
        if (
            (cache[ this.key ][ id ] && replace) ||
            !cache[ this.key ][ id ]
        ) {
            this.cache[ id ] = cache[ this.key ][ id ] = obj;
        }
        return this;
    }
    get(id) {
        return cache[ this.key ][ id ] || undefined;
    }
    remove(id) {
        delete this.cache[ id ];
        delete cache[ this.key ][ id ];
        return this;
    }
    removeAll() {
        this.cache = cache[ this.key ] = {};
        return this;
    }
    delete() {
        delete this.cache;
        delete cache[ this.key ];
    }
}

export default $CacheFactory;

/**
 * @module $Compile.js
 * @author Joe Groseclose <@benderTheCrime>
 * @date 8/16/2015
 */

// System Modules
import {jsdom} from                 'jsdom';
import $LogProvider from            'angie-log';

// Angie Modules
import app from                     '../Angie';
import {$$templateLoader} from      './$TemplateCache';
import $Util, {$StringUtil} from    '../util/Util';

// ngie Incrementer
let iid = 0;

/**
 * @desc $window is provided to any directive which has included it. It
 * is a NodeJS representation of a window object.
 *
 * This should not be used to transform window properties as the window
 * provided by the browser and $window are not the same instance.
 *
 * @since 0.2.4
 * @access public
 * @example new $window.HTMLElement('div');
 */
class $window {}

/**
 * @desc $document is provided to any directive which has included it. It
 * is a NodeJS representation of a window.document object.
 *
 * This should not be used to transform document properties as the document
 * provided by the browser and $document are not the same instance.
 *
 * @since 0.2.4
 * @access public
 * @example new $window.HTMLElement('div');
 */
class $document{}

/**
 * @desc $compile is provided to any service, Controller, directive, Model, or
 * view which has included it as an argument.
 *
 * $compile is responsible for parsing all templates and templatePath files that
 * are passed into a Controller. It will parse all triple bracketted statements,
 * all ngie-native directives, and all custom directives.
 *
 * The triple bracket statement is utilized as a result of the Angular/Mustache/
 * Handlebars standard of two brackets. While you can also do three brackets in
 * either of the latter cases, it is generally considered unsafe and most
 * definitely so in this case.
 *
 * Native directives are called "ngie" to avoid namespace collisions. Dirty
 * checking is performed on directive names for camelCasing,
 * underscore_separation, and dash-separation.
 *
 * It can also be referenced as `app.services.$compile`.
 * @since 0.2.2
 * @todo "No parse operator"
 * @param {string} t Template string to be processed
 * @returns {function} Template function, compiles in whatever scope is passed
 * @access public
 * @example $compile('{{{test}}}')({ test: 1 }) === 1; // true
 */
function $compile(t) {

    if (!t) {
        return $Util.noop;
    }

    // We need to call template.toString() because we did not load with utf8
    let template = t.toString(),
        listeners = template.match(/\{{3}[^\}]+\}{3}/g) || [],
        directives = [];

    // Direct reference by directive name to directive object
    for (let $directive in app.directives) {
        let directive = app.directives[ $directive ];
        directive.$names = [
            $directive,
            $StringUtil.toUnderscore($directive),
            $StringUtil.toDash($directive)
        ];

        // Add all parsed directve names to directives
        directives.push(directive);
    }

    // Sort our directives for priority
    directives.sort(function(a, b) {
        if (!a.priority && !b.priority) {
            return 0;
        }
        return (a.priority && !b.priority) || (a.priority > b.priority) ? 1 : -1;
    });

    /**
     * @desc Function returned by $compile
     * @since 0.2.2
     * @param {object} scope [param={}] Template string to be processed
     * @param {boolean} assignDOMServices [param=true] Create a new
     * $window/$document?
     * @returns {string} The compiled template
     */
    return function $templateCompile (scope = {}, assignDOMServices = true) {

        // Temporary template object, lets us hang on to our template
        let tmpLet = template,
            proms = [];

        // Parse simple listeners/expressions
        listeners.forEach(function(listener) {

            // Remove the bracket mustaches
            let parsedListener = listener.replace(/(\{|\}|\;)/g, '').trim(),
                val = '';

            // Evaluate the expression
            try {
                val = $$evalFn.call(scope, parsedListener);
            } catch(e) {
                $LogProvider.warn(e);
            }

            // Change the scope of the template
            tmpLet = tmpLet.replace(listener, val);
        });

        // Parse directives
        let $$document, $$window;
        try {
            $$document = jsdom(tmpLet, {
                FetchExternalResources: [],
                ProcessExternalResources: false
            });
            $$window = $$document.defaultView;
        } catch(e) {
            $LogProvider.error(e);
        }

        // Assign the window and document services
        if (assignDOMServices === true) {
            app.service('$window', $$window).service('$document', $$document);
        }

        if ($$document.body) {
            let els = $$document.body.querySelectorAll('*');
            for (let i = 0; i < els.length; ++i) {
                let el = els[ i ],
                    type,
                    prom;
                directives.forEach(function(directive) {

                    // Try and match a directive based on type
                    // TODO classList is undefined in jsDOM, polyfill or find a better way
                    if (
                        el.className &&
                        directive.$names.some((v) => el.className.indexOf(v) > -1)
                    ) {
                        type = 'C';
                    } else if (
                        el.hasAttribute &&
                        directive.$names.some((v) => !!(el.hasAttribute(v)))
                    ) {
                        type = 'A';
                    } else if (
                        el.tagName &&
                        directive.$names.indexOf(el.tagName.toLowerCase()) > -1
                    ) {
                        type = 'E';
                    }

                    // Check that the restriction is valid
                    if (
                        type && (
                            !directive.hasOwnProperty('restrict') ||
                            (
                                directive.hasOwnProperty('restrict') &&
                                directive.restrict.indexOf(type) > -1
                            )
                        )
                    ) {
                        prom = $$processDirective(
                            $$document,
                            el,
                            scope,
                            directive,
                            type
                        );
                        proms.push(prom);
                    }
                });
            }
        }

        return Promise.all(proms).then(function() {

            // Check for a document.body in tmpLet
            if (tmpLet.indexOf('<body') > -1 && tmpLet.indexOf('/body>') > -1) {
                tmpLet = `<!DOCTYPE html>\n${$$document.documentElement.outerHTML}`;
            } else {

                // Or return just the string
                tmpLet = $$document.body.innerHTML;
            }

            app.services.$window = app.services.$document = {};
            return tmpLet;
        });
    };
}

// A private function to evaluate the parsed template string in the context of
// `scope`
function $$evalFn(str) {
    let keyStr = '';

    // Perform any parsing that needs to be performed on the scope value
    for (let key in this) {
        let val = this[ key ];
        if (!val) {
            continue;
        } else if (typeof val === 'symbol' || typeof val === 'string') {
            val = `"${val}"`;
        } else if (typeof val === 'object') {
            val = JSON.stringify(val);
        }

        // I don't like having to use var here
        keyStr += `var ${key}=${val};`;
    }

    // Literal eval is executed in its own context here to reduce security issues
    /* eslint-disable */
    return eval([ keyStr, str ].join(''));

    /* eslint-enable */
}

// Private function responsible for parsing directives
function $$processDirective($$document, el, scope, directive, type) {
    let template,
        prom;

    // Template parsing
    if (
        directive.hasOwnProperty('templatePath') &&
        directive.templatePath.indexOf('.html') > -1
    ) {
        template = $$templateLoader(directive.templatePath, 'template', 'utf8');
    } else if (directive.hasOwnProperty('template')) {
        template = directive.template;
    }

    if (template) {

        // Setup the template HTML observing the prepend/append properties
        prom = $compile(template)(scope, false).then(function(t) {
            el.innerHTML =
                `${directive.prepend === true ? '' : el.innerHTML}${t}` +
                `${directive.prepend !== true ? '' : el.innerHTML}`;
        });
    } else {
        prom = new Promise((r) => r());
    }

    // Setup Attrs
    let attrs = el.attributes || {},
        parsedAttrs = {};
    if (el.hasAttribute && el.getAttribute) {
        for (let key in attrs) {
            if (el.hasAttribute(key)) {
                parsedAttrs[ $StringUtil.toCamel(key) ] = el.getAttribute(key);
            }
        }
    }

    // Link functionality
    if (
        directive.hasOwnProperty('link') &&
        typeof directive.link === 'function'
    ) {
        prom = prom.then(function() {
            return new Promise(directive.link.bind(
                app.services.$scope,
                app.services.$scope,
                type !== 'M' ? el : null,
                parsedAttrs
            ));
        }).then(function() {

            // TODO an Observance model would be way better
            if (el.setAttribute) {
                for (let key in parsedAttrs) {

                    // Replace all of the element attrs with parsedAttrs
                    if (directive.$names.indexOf(key) === -1) {
                        el.setAttribute($StringUtil.toDash(key), parsedAttrs[ key ]);
                    }
                }
            }
        });
    }

    // Replace directive keyword
    prom.then(function() {
        if (directive.replace === true) {
            el.setAttribute('ngie-id', ++iid);
            $$document.body.innerHTML = $$document.body.innerHTML.replace(
                el.outerHTML,
                el.innerHTML
            );
        }
    });

    return prom;
}

export default $compile;

/**
 * @module $RouteProvider.js
 * @author Joe Groseclose <@benderTheCrime>
 * @date 8/16/2015
 */

// System Modules
import $LogProvider from    'angie-log';

// Angie Modules
import {$StringUtil} from   '../util/Util';

const IGNORE_KEYS = [
    'Controller',
    'template',
    'templatePath'
];
let routes = {
        '/': {
            templatePath: 'index.html'
        },
        '/404': {
            templatePath: '404.html'
        },
        '/500': {
            templatePath: '404.html'
        }
    },
    otherwise;

/**
 * @desc $RouteProvider is provided to any service, Controller, directive, Model,
 * or view which has included it as an argument as ""$Routes"
 *
 * It can also be referenced as `app.services.$Routes` or imported as
 * '$RouteProvider' from the Angie Path.
 * @since 0.0.1
 */
class $RouteProvider {

    /**
     * @desc Sets up a route as a possible endpoint in an Angie application..
     * @since 0.0.1
     * @param {string|Object} str String or RegExp to denote the endpoint
     * path
     * @param {Object} obj
     * @param {?string} obj.templatePath Optional template path
     * @param {?string} obj.template Optional template html
     * @param {?string} obj.Controller The name of the associated Controller
     * @param {?object} obj.* A deep route with another route object
     * to associate with a route consisting of the original path added to the
     * new key
     * @returns {function} Template function, compiles in whatever scope is
     * passed
     * @access public
     * @example $Routes.when('/test', {
     *     template: '<div>{{{test}}}</div>',
     *     Controller: 'TestCtrl',
     *     test2: {
     *         template: '<div>{{{test2}}}</div>',
               Controller: 'Test2Ctrl'
     *     }
     * });
     */
    static when(path, obj) {
        let regExpFlag = false;

        // Determine if the route is RegExp or not and register the route
        if (path.constructor.name === 'RegExp') {
            regExpFlag = true;

            // Set the route regExp identifier
            if (!routes.regExp) {
                routes.regExp = {};
            }

            // Set a RegExp route
            routes.regExp[ path ] = obj;
        } else {

            // Set a string route
            routes[ path ] = obj;
        }

        // Find any "deep" routes which we may want to add on to the root route
        Object.keys(obj).forEach(function(v) {
            let childPath,
                childObj = obj[ v ];

            // We're talking about a template or a Controller and we don't want
            // that
            // It's worth noting that return based "if"s in Coffee are pretty
            // dope
            if (IGNORE_KEYS.indexOf(v) > -1) {
                return;
            } else if (regExpFlag || /\/.*\//.test(v)) {

                // Check to see if we previously had or now have a path with
                // RegExp
                childPath = $RouteProvider.$stringsToRegExp(
                    path,
                    $StringUtil.removeTrailingLeadingSlashes(v)
                );
            } else {

                // Concatenate two string paths
                childPath = [ path, v ].join('/').replace(/\/\//g, '/');
            }

            // If the child route does not have a Controller, force it to
            // it's parent inherit
            if (obj.Controller && !childObj.Controller) {
                childObj.Controller = obj.Controller;
            }

            // Set a route child object
            $RouteProvider.when(childPath, childObj);

            // Strip the child from the original route object
            delete obj[ v ];
        });
        return this;
    }

    /**
     * @desc Sets a defaulted path in the event that none of the other routes
     * or route patterns are matching. If this is a relative path, it will match
     * absolutely. If this is a full path, it will result in a 301 redirect.
     *
     * @since 0.0.1
     * @param {string} path URL string to set as the "otherwise" path
     * @returns {string} The same URL string
     * @access public
     * @example $Route.otherwise('/test');
     */
    static otherwise(path) {
        if (typeof path === 'string') {
            otherwise = path;
        } else {

            // Redirection can only occur based on this path if path is a string
            $LogProvider.warn(
                'Cannot set "otherwise" route to anything other than a string'
            );
        }
        return path;
    }

    /**
     * @desc Returns all of the set application routes
     * @since 0.0.1
     * @returns {object} Routes object (inc. RegExps) and otherwise path objects
     * @access public
     */
    static fetch() {
        let obj = { routes: routes };
        if (otherwise) {
            obj.otherwise = otherwise;
        }
        return obj;
    }

    /**
     * @desc $Routes.$$clear is a helper method designated to clear out all
     * of the routes in the test environment. Don't call it or you might have a
     * bad time.
     * @since 0.2.5
     * @access private
     * @example $Routes.$$clear() // routes = {};
     */
    static $$clear() {
        routes = {};
        otherwise = undefined;
    }

    /**
     * @desc Take RegExp literals or strings and return a new RexExp
     * @since 0.2.5
     * @param {string|object} str Arguments of strings or RegExp literals
     * @returns {object} Concatenated RegExps joined by "/"
     * @access private
     * @example var regExp = $Routes.$stringsToRegExp(/test/, test);
     *     regExp.test('test/test') === true;
     */
    static $stringsToRegExp() {
        return new RegExp(Array.prototype.slice.call(arguments).map((v) =>
            $StringUtil.removeTrailingLeadingSlashes(v.toString())
        ).join('\\/'));
    }

    /**
     * @desc $Routes.$$parseURLParams is a function that will strip the
     * paramters out of RegExp pattern routes
     *
     * It is a private method
     * @since 0.2.3
     * @param {string} t Template string to be processed
     * @returns {function} Template function, compiles in whatever scope is passed
     * @access private
     * @example $compile('{{{test}}}')({ test: 1 }) === 1; // true
     */
    static $$parseURLParams(pattern, path) {
        let obj = {};

        // If and only if the pattern and path exist
        if (pattern && path) {

            // Strip slashes
            $StringUtil.removeTrailingLeadingSlashes(
                path.replace(pattern, '$1|$2|$3|$4|$5')
            ).split('|').forEach(

                // Pull the paramter from the parsed replace string if there is
                // a match
                function(v, i) {
                    if (v.indexOf(`$${i + 1}`) === -1) {
                        obj[ i ] = v;
                    }
                }
            );
        }

        // Param keys are matched indices and flagged with keys 0-5
        return obj;
    }
}

export default $RouteProvider;

/**
 * @module $TemplateCache.js
 * @author Joe Groseclose <@benderTheCrime>
 * @date 8/16/2015
 */

// System Modules
import fs from                      'fs';
import {default as $Injector} from  'angie-injector';

// Angie Modules
import {config} from                '../Config';
import $CacheFactory from           './$CacheFactory';
import {
    $StringUtil,
    $FileUtil
} from                              '../util/Util';

const p = process,
      ANGIE_TEMPLATE_DIRS = [
          `${__dirname}/../templates/html`,
          `${__dirname}/../../test/src/templates`
      ],
      ANGIE_STATIC_DIRS = [];

class $TemplateCache extends $CacheFactory {
    constructor() {
        super('templateCache');
    }
    get(url) {
        let template = super.get(url);
        if (!template) {
            template = $$templateLoader(url);
        }
        if (template && config.cacheStaticAssets) {
            this.put(url, template);
        }
        return template;
    }
}

function $$templateLoader(url, type = 'template', encoding) {

    // Clone them template dirs
    let templateDirs = (
        config[ `${type}Dirs` ].slice() || []
    ),
    template;

    // Add the default Angie template dirs to the existing config template dirs
    templateDirs = templateDirs.concat(
        type === 'template' ? ANGIE_TEMPLATE_DIRS :
            type === 'static' ? ANGIE_STATIC_DIRS : []
    );

    templateDirs = templateDirs.map(function(dir) {
        if (
            (
                (type === 'template' && ANGIE_TEMPLATE_DIRS.indexOf(dir) === -1) ||
                (type === 'static' && ANGIE_STATIC_DIRS.indexOf(dir) === -1)
            ) &&
            dir.indexOf(p.cwd()) === -1
        ) {
            dir = $StringUtil.removeLeadingSlashes(dir);
            dir = `${p.cwd()}/${dir}`;
        }
        dir = $StringUtil.removeTrailingSlashes(dir);
        return dir;
    });

    // Deliberately use a for loop so that we can break out of it
    for (var i = templateDirs.length - 1; i >= 0; --i) {
        let dir = templateDirs[i],
            path = $FileUtil.find(dir, url);

        if (typeof path === 'string') {
            template = fs.readFileSync(path, encoding || undefined);
        }

        if (template) {
            break;
        }
    }

    if (!template) {
        return false;
    } else if (
        type === 'static' &&
        config.hasOwnProperty('cacheStaticAssets') &&
        config.cacheStaticAssets === true
    ) {
        // TODO you may want to put this in the asset loading block
        new $CacheFactory('staticAssets').put(url, template);
    }
    return template;
}

/**
 * @desc $resourceLoader is a factory that will attach a JavaScript resource
 * to any respose. It will attach it inside of the body if the file is requested
 * to be attached on an HTML response.
 * @since 0.3.2
 * @todo Make this work with .css, .less, .scss, .haml
 * @todo Auto load Angular, jQuery, Underscore, etc. from their names alone
 * via Bower installs
 * @param {string|Array} [param=10] filename Valid JS filename in Angie static
 * directories
 * @param {string} [param='src'] loadStyle How is this resource attached to the
 * document. Options:
 *     'src':       Include a script tag with the name of the resource
 *     'inline':    Include the resource content inline
 * @returns {boolean} Whether this function successfully finished (not an
 * indication that the resource was actually attached)
 * @access public
 * @example $resourceLoader('test.js');
 */
function $resourceLoader(files = [], loadStyle = 'src') {
    let $response = $Injector.get('$response');

    if (!$response || typeof $response !== 'object') {
        return false;
    } else if (!$response.$responseContent) {

        // Just in case the response property was not already defined
        $response.$responseContent = '';
    }

    if (typeof files === 'string') {
        files = [ files ];
    }

    files.forEach(function(resource) {

        // Return if not a js file
        if (resource.split('.').pop() !== 'js') {
            return;
        }

        // TODO put this into a template?
        let asset = '<script type="text/javascript"';
        if (loadStyle === 'src') {
            asset += ` src="${resource}">`;
        } else {
            let assetCache = new $CacheFactory('staticAssets'),
                assetPath = resource.split('/').pop(),
                staticAsset;

            asset += '>';
            if (assetCache.get(assetPath)) {
                staticAsset = assetCache.get(assetPath);
            } else {
                staticAsset = $$templateLoader(assetPath, 'static', 'utf8');
            }

            if (staticAsset.length) {
                asset += `${staticAsset}`;
            }
        }

        asset += '</script>';

        const BODY = '</body>',
            STR = $response.$responseContent;
        if (STR.indexOf(BODY) > -1) {
            let body = STR.lastIndexOf(BODY);

            $response.$responseContent =
                `${STR.substr(0, body)}${asset}${STR.substr(body)}`;
        } else {
            $response.$responseContent = $response.$responseContent + asset;
        }
    });
    return true;
}

const $templateCache = new $TemplateCache();
export {
    $templateCache,
    $$templateLoader,
    $resourceLoader
};

/**
 * @module index.js
 * @author Joe Groseclose <@benderTheCrime>
 * @date 8/16/2015
 */

// Global Modules
import 'es6-module-loader';

// System Modules
import {exec} from                  'child_process';
import {gray} from                  'chalk';
import $LogProvider from            'angie-log';

// Angie Modules
import Config from                  './Config';
import $$createProject from         './util/scaffold/project';
import server from                  './Server';
import shell from                   './util/shell';

// System/Tranform BabelJS options
System.transpiler = 'babel';

const p = process;
let args = [],
    $$server = requiresConfig.bind(null, server),
    $$db = requiresConfig.bind(null, require.bind(null, 'angie-orm')),
    $$shell = requiresConfig.bind(null, shell);

// Remove trivial arguments
p.argv.forEach(function(v) {
    if (!v.match(/(node|iojs|index|angie)/)) {
        args.push(v);
    }
});

// Route the CLI request to a specific command
switch ((args[0] || '').toLowerCase()) {
    case 'help':
        help();
        break;
    case 'server':
        $$server();
        break;
    case 's':
        $$server();
        break;
    case 'cluster':
        break;
    case 'createproject':
        $$createProject({
            name: args[1],
            location: args[2]
        });
        break;
    case 'syncdb':
        $$db();
        break;
    case 'migrate':
        $$db();
        break;
    case 'test':
        runTests();
        break;
    case 'shell':
        $$shell();
        break;
    default:
        $LogProvider.error('Unrecognized CLI Argument');
}

// Wrapper function for services which require configs to be loaded
function requiresConfig(fn) {

    // Fetch configs
    return new Config().then(function() {
        return typeof fn === 'function' ? fn(args) : args;
    }, function() {
        p.exit(1);
    });
}

function runTests() {

    // TODO is there any way to carry the stream output from gulp instead
    // of capturing stdout?
    exec(`cd ${__dirname} && gulp`, function(e, std, err) {
        $LogProvider.info(std);
        if (err) {
            $LogProvider.error(err);
        }
        if (e) {
            throw new Error(e);
        }
    });
}

function help() {
    $LogProvider.bold('Angie');
    console.log('A Component-based NodeJS MVC');
    console.log('\r');
    $LogProvider.bold('Version:');
    console.log(global.ANGIE_VERSION);
    console.log('\r');
    $LogProvider.bold('Commands:');
    console.log(
        'server [ port -- optional ]                        ' +
        gray(
            'Start the Angie Webserver (shortcut with s). Default port ' +
            'is 3000.'
        )
    );
    console.log(
        'cluster [ port -- optional ]                       ' +
        gray('Start the Angie Webserver as a Cluster.')
    );
    console.log(
        'createProject [ name ] [ location -- optional ]    ' +
        gray(
            'Create a new Angie project with the specified name in the ' +
            'current directory.'
        )
    );
    console.log(
        'test                                               ' +
        gray(
            'Runs the Angie test suite and prints the results in the ' +
            'console'
        )
    );
}

